import React from 'react'
import TextField from '@mui/material/TextField';
import { Avatar, Button, Checkbox, FormControlLabel, Grid, Paper } from '@mui/material';


import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';

const PatientForm = () => {
    const [patientType, setPatientType] = React.useState('');

    const handleChange = (event: SelectChangeEvent) => {
        setPatientType(event.target.value);
    };

    const paperStyle = { padding: 20, height: '50vh', width: 280, margin: "20px auto" }
    const avatarStyle = { backgroundColor: '#1bbd7e' }
    const textStyle = { paddingBottom: 20, marginTop: 20 }
    return (
        <div className="loginContainer">
            <FormControl variant="standard" sx={{ m: 1, minWidth: 150 }}>
                <InputLabel id="demo-simple-select-standard-label">Patient Type</InputLabel>
                <Select
                    labelId="demo-simple-select-standard-label"
                    id="demo-simple-select-standard"
                    value={patientType}
                    onChange={handleChange}
                    label="Patient Type" required
                >
                    <MenuItem value='IN_PATIENT'>In Patient</MenuItem>
                    <MenuItem value='OUT_PATIENT'>Out Patient</MenuItem>
                    <MenuItem value='EMERGENCY'>Emergency</MenuItem>
                </Select>
            </FormControl>

            <TextField label='Patient Type' placeholder='Enter username' variant='standard' style={textStyle} fullWidth required
            />
            <TextField label='Password' placeholder='Enter password' variant='standard' style={textStyle} type='password' fullWidth required
            />
            <Button type='submit' color='primary' fullWidth variant='contained'
            >Sign In</Button>
        </div>
    )
}

export default PatientForm
