import React from 'react'
import TextField from '@mui/material/TextField';
import { Avatar, Button, Checkbox, FormControlLabel, Grid, Paper } from '@mui/material';


import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import { useState } from 'react';

const PatientForm = () => {
    const [patientType, setPatientType] = useState('OUT_PATIENT');
    const [gender, setGender] = useState('M');
    const [name, setName] = useState(null);
    const [email, setEmail] = useState(null);
    const [phone, setPhone] = useState(null);
    const [address, setAddress] = useState(null);
    const [bloodGroup, setBloodGroup] = useState(null);
    const [alternatePhone, setAlternatePhone] = useState(null);

    const paperStyle = { padding: 20, height: '50vh', width: 280, margin: "20px auto" }
    const avatarStyle = { backgroundColor: '#1bbd7e' }
    const textStyle = { paddingBottom: 20, marginTop: 20 }
    return (
        <div className="loginContainer">
            <FormControl variant="standard" sx={{ m: 1, minWidth: 300 }}>
                <InputLabel id="demo-simple-select-standard-label">Patient Type</InputLabel>
                <Select
                    labelId="demo-simple-select-standard-label"
                    id="demo-simple-select-standard"
                    label="Patient Type"
                    onChange={(e) => {
                        setPatientType(e.target.value)
                    }}
                >
                    <MenuItem value='IN_PATIENT'>In Patient</MenuItem>
                    <MenuItem value='OUT_PATIENT'>Out Patient</MenuItem>
                    <MenuItem value='EMERGENCY'>Emergency</MenuItem>
                </Select>

                <TextField label='Full Name' placeholder='Enter full name' variant='standard' style={textStyle} fullWidth required />
                <TextField label='Phone' placeholder='Enter phone' variant='standard' style={textStyle} fullWidth required
                />
                <TextField label='Alternate Phone' placeholder='Enter alt phone' variant='standard' style={textStyle} fullWidth required
                />
            </FormControl>
            <FormControl variant="standard" sx={{ m: 1, minWidth: 300 }}>
                <InputLabel id="gender-label">Gender</InputLabel>
                <Select
                    labelId="gender-label"
                    id="gender"
                    label="Gender" required
                >
                    <MenuItem value='M'>Male</MenuItem>
                    <MenuItem value='F'>Female</MenuItem>
                    <MenuItem value='O'>Other</MenuItem>
                </Select>
                <TextField label='Email' placeholder='Enter email' variant='standard' type='email' style={textStyle} fullWidth required />
                <TextField label='Address' placeholder='Enter address' variant='standard' style={textStyle} fullWidth required
                />
                <TextField label='Blood Group' placeholder='Enter blood grp' variant='standard' style={textStyle} fullWidth required
                />
            </FormControl>

            <Button type='submit' color='primary' fullWidth variant='contained'
            >Save</Button>
        </div>
    )
}

export default PatientForm
